package com.test.day23.string;

import org.junit.jupiter.api.Test;

public class AmazingSubstring {
    @Test
    public void test(){
        String s = "";
        System.out.println(solve(s));
    }

    public int solve(String A) {
        String[] arr = A.split("");
        int c = 0;
        int n = arr.length;
        for(int i=0; i<n; i++){
            if(isVowel(arr[i])){
                c = c + ((n-i)%1003);
            }
        }
        return c;
    }

    private boolean isVowel(String s){
        s = s.toUpperCase();
        return s.equals("A")||s.equals("E")||s.equals("I")||s.equals("O")||s.equals("U");
    }
}
